CREATE TABLE "public"."DiscordAuth" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "timeCreated" timestamptz NOT NULL DEFAULT now(), "userId" text NOT NULL, "discordUserId" text NOT NULL, "discordHandle" text NOT NULL, "hasConnectedDiscordAccount" boolean NOT NULL, "hasJoinedDiscordServer" boolean NOT NULL, "hasDismissedDiscordOnboardingPrompt" boolean NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("userId") REFERENCES "public"."User"("id") ON UPDATE restrict ON DELETE restrict, UNIQUE ("userId"), UNIQUE ("id"), UNIQUE ("discordUserId"));COMMENT ON TABLE "public"."DiscordAuth" IS E'Records Discord account information for users to facilitate automatic Discord role provisioning';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
