CREATE TABLE "public"."Notification" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "data" jsonb NOT NULL, "receiver" text NOT NULL, "sender" text NOT NULL, "timeCreated" timestamptz NOT NULL DEFAULT now(), "type" text NOT NULL, PRIMARY KEY ("id") , FOREIGN KEY ("receiver") REFERENCES "public"."User"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("sender") REFERENCES "public"."User"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("type") REFERENCES "public"."NotificationType"("value") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));COMMENT ON TABLE "public"."Notification" IS E'A persistent log of all notifications that we want to send.';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
