CREATE TABLE "public"."NotificationUserPreference" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "userId" text NOT NULL, "notificationType" text NOT NULL, "notificationChannel" text NOT NULL, "enabled" boolean NOT NULL, PRIMARY KEY ("userId","notificationType","notificationChannel") , FOREIGN KEY ("userId") REFERENCES "public"."User"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("notificationType") REFERENCES "public"."NotificationType"("value") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("notificationChannel") REFERENCES "public"."NotificationChannel"("value") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"));
CREATE EXTENSION IF NOT EXISTS pgcrypto;
