CREATE TABLE "public"."Campaign" ("id" uuid NOT NULL, "colorScheme" text NOT NULL, "category" text NOT NULL, "creatorId" text NOT NULL, "endTime" timestamptz, "fundingTierOrder" jsonb, "previewAssetId" uuid NOT NULL, "slug" text NOT NULL, "status" text NOT NULL, "tagline" text NOT NULL, "timeCreated" timestamptz NOT NULL DEFAULT now(), "title" text NOT NULL, "youtubeVideoHref" text, "goalProgressSymbol" text NOT NULL, "goalCurrencyId" uuid, "goalAmount" int8 NOT NULL, "goalType" text NOT NULL, "aboutTheCampaign" text, "aboutTheCreator" text, "aboutTimeline" text, "aboutRisksAndChallenges" text, "aboutContactInfo" text, PRIMARY KEY ("id") , FOREIGN KEY ("colorScheme") REFERENCES "public"."CampaignColorScheme"("value") ON UPDATE cascade ON DELETE restrict, FOREIGN KEY ("category") REFERENCES "public"."CampaignCategory"("value") ON UPDATE cascade ON DELETE restrict, FOREIGN KEY ("creatorId") REFERENCES "public"."User"("id") ON UPDATE cascade ON DELETE restrict, FOREIGN KEY ("previewAssetId") REFERENCES "public"."Asset"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("status") REFERENCES "public"."CampaignStatus"("value") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("goalCurrencyId") REFERENCES "public"."Currency"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("goalType") REFERENCES "public"."CampaignGoalType"("value") ON UPDATE restrict ON DELETE restrict, UNIQUE ("id"), UNIQUE ("creatorId", "slug"));
