CREATE TABLE "public"."Reaction" ("id" uuid NOT NULL DEFAULT gen_random_uuid(), "timeCreated" timestamptz NOT NULL DEFAULT now(), "type" text NOT NULL, "fromUserId" text NOT NULL, "postId" uuid, PRIMARY KEY ("id") , FOREIGN KEY ("postId") REFERENCES "public"."Post"("id") ON UPDATE cascade ON DELETE cascade, FOREIGN KEY ("fromUserId") REFERENCES "public"."User"("id") ON UPDATE cascade ON DELETE cascade, UNIQUE ("id"), UNIQUE ("fromUserId", "postId"));COMMENT ON TABLE "public"."Reaction" IS E'Table that stores various reactions for things posts/comments/etc.';
CREATE EXTENSION IF NOT EXISTS pgcrypto;
